import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import { Box, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Typography, Toolbar, Container } from '@mui/material';
import api from '../../services/api';
import Sidebar from '../common/Sidebar';
import Header from '../common/Header';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await api.get('/products');
      if (response.data.state) {
        setProducts(response.data.data);
      } else {
        setError(response.data.message);
        Swal.fire('Error', response.data.message, 'error');
      }
    } catch (err) {
      setError('Failed to fetch products');
      Swal.fire('Error', 'No se pudieron obtener los productos', 'error');
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <Header />
      <Box sx={{ display: 'flex', flexGrow: 1 }}>
        <Sidebar />
        <Box component="main" sx={{ flexGrow: 1, p: 3, overflow: 'auto' }}>
          <Toolbar />
          <Container maxWidth="lg">
            <Typography variant="h4" gutterBottom>
              Products
            </Typography>
            {/* ...existing code... */}
            <TableContainer component={Paper} sx={{ overflowX: 'auto' }}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Description</TableCell>
                    <TableCell>Price</TableCell>
                    <TableCell>Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {products.map((product) => (
                    <TableRow key={product.id}>
                      <TableCell>{product.id}</TableCell>
                      <TableCell>{product.name}</TableCell>
                      <TableCell>{product.description}</TableCell>
                      <TableCell>{product.price}</TableCell>
                      <TableCell>
                        <Button size="small">Edit</Button>
                        <Button size="small" color="error" onClick={() => handleDelete(product.id, product.name)}>Delete</Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Container>

        </Box>
      </Box>
    </Box>
  );
};

// Confirmación y borrado de producto
// Nota: handleDelete debe estar dentro del componente para acceder a fetchProducts



export default ProductList;
