import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import {
  Box, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Typography, Toolbar, Container, Dialog, DialogTitle, DialogContent, DialogActions, TextField, InputAdornment
} from '@mui/material';
import api from '../../services/api';
import Header from '../common/Header';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [limit] = useState(10);
  const [totalPages, setTotalPages] = useState(1);
  const [total, setTotal] = useState(0);
  const [openForm, setOpenForm] = useState(false);
  const [editProduct, setEditProduct] = useState(null);
  const [form, setForm] = useState({
    code: '',
    name: '',
    description: '',
    pvp: '',
    min: '',
    max: '',
    state: 1
  });

  useEffect(() => {
    fetchProducts(page);
  }, [page]);

  const fetchProducts = async (pageNum = 1) => {
    try {
      const response = await api.get('/products');
      if (response.data && response.data.state === 1 && Array.isArray(response.data.data)) {
        setProducts(response.data.data);
        setTotalPages(Math.ceil(response.data.data.length / limit));
        setTotal(response.data.data.length);
        setError('');
      } else {
        setProducts([]);
        setTotalPages(1);
        setTotal(0);
        setError(response.data?.message || 'No se encontraron productos.');
      }
    } catch (err) {
      setProducts([]);
      setTotalPages(1);
      setTotal(0);
      setError('Error al obtener productos');
    }
  };

  const handleFormChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleEdit = (product) => {
    setEditProduct(product);
    setForm({
      code: product.code_product || '',
      name: product.name_product || '',
      description: product.description_product || '',
      pvp: product.pvp_product || '',
      min: product.min_product || '',
      max: product.max_product || '',
      state: product.state_product ?? 1
    });
    setOpenForm(true);
  };

  const handleDelete = async (product) => {
    const result = await Swal.fire({
      title: '쮼liminar producto?',
      text: `쯉eguro que deseas eliminar el producto ${product.name_product}?`,
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'S칤, eliminar',
      cancelButtonText: 'Cancelar',
    });
    if (result.isConfirmed) {
      try {
        const response = await api.delete(`/products/${product.id_product}`);
        if (response.data.state) {
          Swal.fire('Eliminado', 'Producto eliminado correctamente', 'success');
          fetchProducts(page);
        } else {
          Swal.fire('Error', response.data.message, 'error');
        }
      } catch (err) {
        Swal.fire('Error', 'No se pudo eliminar el producto', 'error');
      }
    }
  };

  const handleCreateOrUpdateProduct = async () => {
    if (!form.code.trim() || !form.name.trim()) {
      Swal.fire({
        icon: 'warning',
        title: 'Campos requeridos',
        text: 'Los campos C칩digo y Nombre son obligatorios.',
      });
      return;
    }
    try {
      let response;
      const payload = {
        code_product: form.code,
        name_product: form.name,
        description_product: form.description,
        pvp_product: form.pvp,
        min_product: form.min,
        max_product: form.max,
        state_product: form.state
      };
      if (editProduct) {
        response = await api.put(`/products/${editProduct.id_product}`, payload);
      } else {
        response = await api.post('/products', payload);
      }
      if (response.data && response.data.state) {
        setOpenForm(false);
        setEditProduct(null);
        setForm({ code: '', name: '', description: '', pvp: '', min: '', max: '', state: 1 });
        setPage(1);
        fetchProducts(1);
        Swal.fire({
          icon: 'success',
          title: '칄xito',
          text: editProduct ? 'Producto actualizado correctamente' : 'Producto creado correctamente',
        });
      } else {
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: (response.data && response.data.message) ? response.data.message : 'No se pudo guardar el producto. Verifica los datos.',
        });
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: err.response?.data?.message || err.message || (editProduct ? 'No se pudo actualizar el producto' : 'No se pudo crear el producto'),
      });
    }
  };

  // Filtro de b칰squeda frontend y paginaci칩n
  const filteredProducts = products.filter((product) => {
    const searchLower = search.toLowerCase();
    const name = product.name_product || '';
    const code = product.code_product || '';
    const desc = product.description_product || '';
    return (
      !search ||
      name.toLowerCase().includes(searchLower) ||
      code.toLowerCase().includes(searchLower) ||
      desc.toLowerCase().includes(searchLower)
    );
  });
  const paginatedProducts = filteredProducts.slice((page - 1) * limit, page * limit);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <Header />
      <Box sx={{ display: 'flex', flexGrow: 1 }}>
        <Box component="main" sx={{ flexGrow: 1, p: 3, overflow: 'auto' }}>
          <Toolbar />
          <Container maxWidth="lg">
            <Typography variant="h4" gutterBottom>
              Productos
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2, gap: 2, flexWrap: 'wrap' }}>
              <Button variant="contained" color="primary" onClick={() => { setOpenForm(true); setEditProduct(null); setForm({ code: '', name: '', description: '', pvp: '', min: '', max: '', state: 1 }); }}>
                Agregar Producto
              </Button>
              <TextField
                label="Buscar por c칩digo, nombre o descripci칩n"
                variant="outlined"
                size="small"
                value={search}
                onChange={e => setSearch(e.target.value)}
                sx={{ minWidth: 250 }}
                InputProps={{
                  startAdornment: <InputAdornment position="start">游댌</InputAdornment>,
                }}
              />
            </Box>
            <TableContainer component={Paper} sx={{ overflowX: 'auto', boxShadow: 3, borderRadius: 2, mb: 2 }}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 80 }}>ID</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 120 }}>C칩digo</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 180 }}>Nombre</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 220 }}>Descripci칩n</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 120 }}>PVP</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 120 }}>M칤nimo</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 120 }}>M치ximo</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 120 }}>Estado</TableCell>
                    <TableCell align="center" sx={{ fontWeight: 'bold', width: 180 }}>Acciones</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {paginatedProducts.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={9} align="center">No hay productos para mostrar.</TableCell>
                    </TableRow>
                  ) : (
                    paginatedProducts.map((product) => (
                      <TableRow key={product.id_product} hover>
                        <TableCell align="center">{product.id_product}</TableCell>
                        <TableCell align="center">{product.code_product}</TableCell>
                        <TableCell align="center">{product.name_product}</TableCell>
                        <TableCell align="center">{product.description_product}</TableCell>
                        <TableCell align="center">{product.pvp_product}</TableCell>
                        <TableCell align="center">{product.min_product}</TableCell>
                        <TableCell align="center">{product.max_product}</TableCell>
                        <TableCell align="center">{product.state_product === 1 ? 'Activo' : 'Inactivo'}</TableCell>
                        <TableCell align="center">
                          <Button size="small" variant="outlined" sx={{ mr: 1 }} onClick={() => handleEdit(product)}>Editar</Button>
                          <Button size="small" variant="outlined" color="error" onClick={() => handleDelete(product)}>Eliminar</Button>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Paginaci칩n */}
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', mt: 2 }}>
              <Button
                variant="outlined"
                onClick={() => setPage((prev) => Math.max(prev - 1, 1))}
                disabled={page === 1}
                sx={{ mr: 1 }}
              >
                Anterior
              </Button>
              <Typography sx={{ mx: 2 }}>
                P치gina {page} de {Math.max(1, Math.ceil(filteredProducts.length / limit))} ({filteredProducts.length} productos)
              </Typography>
              <Button
                variant="outlined"
                onClick={() => setPage((prev) => Math.min(prev + 1, Math.max(1, Math.ceil(filteredProducts.length / limit))))}
                disabled={page === Math.max(1, Math.ceil(filteredProducts.length / limit))}
                sx={{ ml: 1 }}
              >
                Siguiente
              </Button>
            </Box>
            {/* Formulario modal para crear/editar producto */}
            <Dialog open={openForm} onClose={() => { setOpenForm(false); setEditProduct(null); }}>
              <DialogTitle>{editProduct ? 'Editar Producto' : 'Nuevo Producto'}</DialogTitle>
              <DialogContent>
                <TextField
                  margin="dense"
                  label="C칩digo"
                  name="code"
                  value={form.code}
                  onChange={handleFormChange}
                  fullWidth
                  required
                />
                <TextField
                  margin="dense"
                  label="Nombre"
                  name="name"
                  value={form.name}
                  onChange={handleFormChange}
                  fullWidth
                  required
                />
                <TextField
                  margin="dense"
                  label="Descripci칩n"
                  name="description"
                  value={form.description}
                  onChange={handleFormChange}
                  fullWidth
                />
                <TextField
                  margin="dense"
                  label="PVP"
                  name="pvp"
                  type="number"
                  value={form.pvp}
                  onChange={handleFormChange}
                  fullWidth
                />
                <TextField
                  margin="dense"
                  label="M칤nimo"
                  name="min"
                  type="number"
                  value={form.min}
                  onChange={handleFormChange}
                  fullWidth
                />
                <TextField
                  margin="dense"
                  label="M치ximo"
                  name="max"
                  type="number"
                  value={form.max}
                  onChange={handleFormChange}
                  fullWidth
                />
              </DialogContent>
              <DialogActions>
                <Button onClick={() => { setOpenForm(false); setEditProduct(null); }}>Cancelar</Button>
                <Button onClick={handleCreateOrUpdateProduct} variant="contained">{editProduct ? 'Actualizar' : 'Crear'}</Button>
              </DialogActions>
            </Dialog>
          </Container>
        </Box>
      </Box>
    </Box>
  );
};

export default ProductList;
